Ext.define('ASTD.view.catalog.CatalogListViewModel', {
    extend: 'Ext.app.ViewModel',
    alias: 'viewmodel.catalog.cataloglistview',

    requires: [
        'Ext.data.TreeStore',
        'Ext.app.bind.Formula'
    ],

    stores: {
        catalogStore: {
            type: 'tree',
            remoteFilter: true,
            autoLoad: true,
            model: 'ASTD.model.CatalogModel',
            listeners: {
                beforeload: 'onCatalogStoreBeforeLoad',
                load: 'onCatalogStoreLoad'
            }
        }
    },
    formulas: {
        isCreateBtnHidden: function(get) {
            var catalog = get('current.catalog');
            var user = this.get('current.user');
            if(!catalog || !user || !catalog.get('isCatalog')) {
                return true;
            }
            return catalog.get('type') === 'SCHEME' || catalog.get('type') === 'ROOT' ||
            user.get('positionId') !== 5;
        },
        isEditBtnHidden: function(get) {
            var catalog = get('current.catalog');
            var user = this.get('current.user');
            if(!catalog || !user || !catalog.get('isCatalog')) {
                return true;
            }
            return catalog.get('type') === 'ROOT' || catalog.get('type') === 'STATION' || catalog.get('type') === 'LINE' ||
            user.get('positionId') !== 5;
        },
        isDeleteBtnHidden: {
            get: function(data) {
                // var catalog = data('current.catalog');
                var catalog = data;
                var user = this.get('current.user');
                if(!catalog || !user || !catalog.get('isCatalog')) {
                    return true;
                }
                return catalog.get('type') === 'ROOT' || catalog.get('type') === 'STATION' || catalog.get('type') === 'LINE' ||
                catalog.get('readOnly') ||
                user.get('positionId') !== 5;
            },
            bind: {
                bindTo: '{current.catalog}',
                deep: true
            }
        }
    }

});