Ext.define('ASTD.view.doc.DocListViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.doc.doclistview',

    onCompleteDelete: function(record, operation, success) {
        Ext.Msg.hide();
        if(success){
            if(this.getView().getStore().count() === 1){
                this.getViewModel().get('current.catalog').set('readOnly', false);
            }
            Ext.Msg.wait("Загрузка", "Загрузка списка");
            this.getView().getStore().reload();
        } else {
            record.dropped = false;
        }
        Ext.toast({
            title: 'Удаление',
            html: success ? 'Документ успешно удален' : 'Ошибка',
            align: 'tr',
            bodyPadding: 10
        });
    },

    onCreateBtnClick: function(button, e, eOpts) {
        if(!ASTD.Utils.isSelected(this.getViewModel().get('current.catalog'))){
            return false;
        }

        var currentCatalog = this.getViewModel().get('current.catalog');
        var currentDoc = Ext.create('ASTD.model.DocModel', {
            catalogId: currentCatalog.getId(),
            rootCatalogId: ASTD.Utils.getRootCatalog(currentCatalog).getId()
        });

        var win = new ASTD.view.doc.DocFormView({
            viewModel: {
                data: {
                    currentDoc: currentDoc,
                    docList: this.getView(),
                    currentCatalog: currentCatalog,
                    listViewModel: this.getViewModel()
                }
            }
        });

        win.show();
    },

    onEditBtnClick: function(button, e, eOpts) {
        if(!ASTD.Utils.isSelected(this.getView().getSelection()[0])){
            return false;
        }

        var win = new ASTD.view.doc.DocFormView({
            viewModel: {
                data: {
                    currentDoc: this.getView().getSelection()[0],
                    docList: this.getView()
                }
            }
        });

        win.show();
    },

    onDeleteBtnClick: function(button, e, eOpts) {
        if(!ASTD.Utils.isSelected(this.getView().getSelection()[0])){
            return false;
        }
        Ext.Msg.show({
            title:'Удалить?',
            message: 'Вы действительно хотите удалить?',
            buttons: Ext.Msg.YESNO,
            icon: Ext.Msg.QUESTION,
            scope: this,
            fn: function(btn) {
                if(btn === 'yes') {
                    var currentDoc = this.getView().getSelection()[0];
                    Ext.Msg.wait('Удаление', 'Удаление файла...');
                    currentDoc.dropped = true;
                    currentDoc.save({
                        scope: this,
                        callback: this.onCompleteDelete
                    });
                }
            }
        });
    },

    onFilesBtnClick: function(button, e, eOpts) {
        var record = this.getView().getSelection()[0];
        if(!ASTD.Utils.isSelected(record)){
            return false;
        }
        this.fireViewEvent('clickDocList', this.getView(), record);
    },

    onDocItemDblClick: function(dataview, record, item, index, e, eOpts) {
        this.fireViewEvent('clickDocList', this.getView(), record);
    },

    onStoreLoad: function(store, records, successful, operation, eOpts) {
        this.getView().getSelectionModel().deselectAll();
    }

});
