Ext.define('ASTD.view.doc.DocListViewModel', {
    extend: 'Ext.app.ViewModel',
    alias: 'viewmodel.doc.doclistview',

    requires: [
        'Ext.data.Store',
        'Ext.app.bind.Formula'
    ],

    stores: {
        docStore: {
            remoteFilter: true,
            remoteSort: true,
            model: 'ASTD.model.DocModel',
            listeners: {
                load: 'onStoreLoad'
            }
        }
    },
    formulas: {
        isCreateBtnHidden: function(get) {
            var isHidden = false;
            var catalog = get('current.catalog');
            var user = this.get('current.user');
            if(!catalog || !user) {
                return true;
            }

            isHidden = user.get('positionId') !== 5 && user.get('positionId') !== 9;
            return isHidden;
        },
        isEditBtnHidden: function(get) {
            var isHidden = false;
            var catalog = get('current.catalog');
            var user = this.get('current.user');
            if(!catalog || !user) {
                return true;
            }


            isHidden = user.get('positionId') !== 5 && user.get('positionId') !== 9;

            if(!isHidden) {
                var doc = get('current.doc');
                if(!doc) {
                    return isHidden;
                }
            }

            return isHidden;
        },
        isDeleteBtnHidden: function(get) {
            var isHidden = false;
            var catalog = get('current.catalog');
            var user = this.get('current.user');
            if(!catalog || !user) {
                return true;
            }

            isHidden = user.get('positionId') !== 5 && user.get('positionId') !== 9;

            if(!isHidden) {
                var doc = get('current.doc');
                if(!doc) {
                    return isHidden;
                }
                return doc.get('readOnly');
            }

            return isHidden;
        },
        breadCrumbs: function(get) {
            var catalog = get('current.catalog');
            if(!catalog || !catalog.parentNode || !catalog.parentNode.parentNode) {
                return '';
            }
            return catalog.parentNode.parentNode.data.name + ' > ' + catalog.parentNode.data.name + ' > ' +
            catalog.data.name  + ' > ';
        }
    }

});